$(document).on("click",".show-modal-project",function(e){try{e.preventDefault();const o=JSON.parse(e.target.dataset.project),t=document.querySelector(`#image-project-${o.id}`),c=$("#projectModal");c.find(".image").attr("src",t.src),c.find(".resume").text(o.resume),c.find(".client").text(o.client),c.find(".power").text(o.power_system+"kWp"),c.find(".economy-money").text(o.economy_money),c.find(".economy-co2").text(o.economy_co2.replace(/\.00$/,"")),c.modal("show")}catch(e){console.log(e),console.log("Falha ao setar os dados na modal")}});
